import React, {useState, createContext, Children} from "react"; 

export const AuthContext = createContext({
    user: undefined // datos que van a poder acceder todos los componentes
}) // declaramos nuestro contexto e importamos,

// Provaider son todas las acciones que van hacer nuestro conexto, por ejemplo para hacer login y logout

export function AuthProvider(props) { //
    //va recibir todas las funcionalidades como por ejemplo el de NavigationContainer que le da props a todos los otros contenedores como Navigate

    const {chilren} = props;
    const {auth, setAuth} = useState(undefined);

    const valueContext = {
        auth,
    }

    return (
        <AuthContext.Provider value={valueContext}></AuthContext.Provider>
    )
}
