import { Text, View, StyleSheet, Image, TouchableOpacity, TextInput} from 'react-native'
import React from 'react'
import CardComercio from '../Components/CarComercio';

export default function Inicio (props) {
  console.log(props)

  const {navigation} = props;

  const goToSettings= () => {
    navigation.navigate("Settings")
  }
    return (
      <View>
        <View style={styles.containerMenu}>
          <Image source={require('../../assets/SaveFood.png')} />
          <TextInput style={styles.input} placeholder="Busqueda"/>
          <Image source={require("../../assets/busqueda.png")} />
        </View>
        <View style={styles.container}>
          <TouchableOpacity
            style={styles.button}
            onPress={goToSettings}>
            <Text style={styles.text}>Explorar</Text>
          </TouchableOpacity>
        </View>
  
        <Text style={styles.title}>Bienvenido!</Text>

        <View style={styles.containerTop}>
          <Text>TOP 10</Text>
          <Image source={require('../../assets/copa.png')} />
        </View>

        <CardComercio/>
        <CardComercio/>

        <Text style={styles}>Menu botones abajo</Text>
      </View>
    )
}

const styles = StyleSheet.create({
  containerMenu:{
    flexDirection:'row',
    justifyContent:'space-around'
  },
  button:{
    padding:12,
    backgroundColor: '#FAD586',
    height:50,
    width:250,
    marginTop:20,
    borderRadius:15,
  },
  container:{
    marginLeft:'20%',
    marginTop:15
  },
  text:{
    textAlign:'center',
    fontSize:18,
    color:'#184D47',
  },
  title:{
    textAlign:'center',
    fontSize:20,
    marginTop:15
  },
  containerTop:{
    flexDirection:'row',
    justifyContent:'center',
    marginTop:15,
    marginBottom:15,
  },
  input:{
    borderWidth: 1,
    padding: 5,
    borderRadius:15,
    width:150,
    borderColor:'#184D47'
  }

})
